html:
{% set member = frappe.db.get_value("User", doc.member, ["full_name"], as_dict=True) %}
{% set course = frappe.db.get_value("LMS Course", doc.course, ["title", "name", "image"], as_dict=True) %}
{% set instructors = frappe.get_all("Course Instructor", {"parent": doc.course}, pluck="instructor", order_by="idx") %}
{% set instructor_names = [] %}
{% for instructor in instructors -%}
{{ instructor_names.append(frappe.db.get_value("User", instructor, "full_name")) }}
{% endfor -%}
{% set logo = frappe.db.get_single_value("Website Settings", "banner_image") %}

<div id="certificate-card" style="border-radius: 0.5rem; position: relative;
    box-shadow: 0px 1px 2px rgba(25, 39, 52, 0.05), 0px 0px 4px rgba(25, 39, 52, 0.1); padding: 1rem;">

    <div style="background: var(--primary) !important; display: flex; flex-direction: column; align-items: center; padding: 4rem;
        justify-content: center; background-color: #FFFFFF;">

        <img src="{{ logo }}" style="height: 1.5rem;">
        <div style="margin-top: 4rem;">
            {{ _("This certifies that") }}
        </div>

        <div style="font-size: 2rem; font-weight: 500; color: #192734;">
            {{ member.full_name }}
        </div>
        <div style="margin-top: 0.5rem;">
            {{ _("has successfully completed the course on") }}
            <b> {{ course.title }} </b>
            on {{ frappe.utils.format_date(doc.issue_date, "medium") }}.
        </div>

        <div style="display: flex; justify-content: center; margin: 4rem auto 0; width: fit-content;">
            {% if instructors | length %}
            <div>
                <div style="color: #192734; font-weight: bold; font-family: cursive; font-size: 1.25rem;">
                    {{ (", ").join(instructor_names) }}
                </div>
                <hr style="margin: 0.5rem 0;">
                <div class="text-center"> {{ _("Course Instructor") }} </div>
            </div>
            {% endif %}

            {% if doc.expiry_date %}
            <div style="margin-left: 2rem;">
                <div style="color: #192734; font-weight: bold; font-family: cursive; font-size: 1.25rem;">
                    {{ frappe.utils.format_date(doc.expiry_date, "medium") }}
                </div>
                <hr style="margin: 0.5rem 0;">
                <div class="text-center"> {{ _("Expiry date") }} </div>
            </div>
            {% endif %}
        </div>
    </div>
</div>
